import { useState } from "react";
import { runPrompt }from "./GPT/useGPT"

const Terminal = () => {
    const [idea, setIdea] = useState("")


    const createIdea = async () => {
        const json = await runPrompt("very hard");
        setIdea(JSON.parse(json))
    }

    return ( 
        <div className=" p-4 relative w-5/6 h-5/6 bg-neutral-900 rounded-3xl  border-neutral-900 border-t-8 text-white">
            <p className="">{idea.project}</p>
            <p className="">{idea.languages}</p>
            {idea.frameworks.map((framework) => (
                    <p>{framework}</p>                
            ))}  
            <p className="p">{idea.description}</p>
            <p className="p-4">{idea.features}</p>
            <p className="p-4">{idea.difficulty}</p>
            <button className="absolute bottom-0 text-4xl pl-4" onClick={() => createIdea()}>Generate New Project Idea</button>
        </div>
     );
}

export default Terminal;
