import { useState } from "react";
import { runPrompt }from "./GPT/useGPT"

const Terminal = () => {
    const [idea, setIdea] = useState(null)
    const [loading, setLoading] = useState(true)
    const [difficulty, setDifficulty] = useState("Easy")
    this.handleChange = this.hand
    const createIdea = async () => {
        setIdea(null)
        setLoading(false)
        const json = await runPrompt(difficulty);

        try {
        setIdea(JSON.parse(json))
        setLoading(true)
        } catch(e){
            console.log(e)
            createIdea()
        }
    }
    return ( 
        <div className="p-10 relative w-5/6 h-5/6 bg-neutral-900 rounded-3xl  border-neutral-900 border-t-8 text-white">
            {!loading && <div>Loading...</div>}
            {idea && <p className="p-4">Idea: {idea.project}</p>}
            {idea && <p className="p-4">{idea.description}</p>}
            {idea && <span className="p-4">Languages: </span>}
            {idea && idea.languages.map((language) => (
                <span className="p-4">{language}</span>                
            ))} 
            <br/>
            {idea &&<span className="p-4">Frameworks: </span> }
            {idea && idea.frameworks.map((framework) => (
                <span className="p-4">{framework}</span>                
            ))} 
            <br/>
            {idea && <span className="p-4">Features: </span>}
            {idea && idea.features.map((feature) => (
                    <span className="p-4">{feature}</span>                
            ))}  
            {idea && <p className="p-4">Difficulty: {idea.difficulty}</p>}
            {loading && 
                <div>
                <select onChange={handleChange()} id="difficulty">
                    <option selected value="Easy">Easy</option>
                    <option value="Medium">Medium</option>
                    <option value="Hard">Hard</option>
                <button className="absolute bottom-0 text-4xl pl-4">Generate New Project Idea</button>
                </select>
                 </div>

            }
        </div>
     );

}

export default Terminal;
