import { useState } from "react";
import { runPrompt }from "./GPT/useGPT"

const Terminal = () => {
    const [idea, setIdea] = useState(null)


    const createIdea = async () => {
        const json = await runPrompt("hard");
        setIdea(JSON.parse(json))
    }

    return ( 
        <div className="p-10 relative w-5/6 h-5/6 bg-neutral-900 rounded-3xl  border-neutral-900 border-t-8 text-white">
            {idea && <p className="p-4">Idea: {idea.project}</p>}
            {idea && <span>Languages: </span>}
            {idea && idea.languages.map((language) => (
                <span className="p-4">{language}</span>                
            ))} 
            {idea && idea.frameworks.map((framework) => (
                <p className="p-4">Framework: {framework}</p>                
            ))} 
            {idea && <p className="">{idea.description}</p>}
            {idea && <span>Fea: </span>}
            {idea && idea.features.map((feature) => (
                    <p className="p-4">Features: {feature}</p>                
            ))}  
            {idea && <p className="">Difficulty: {idea.difficulty}</p>}
            <button className="absolute bottom-0 text-4xl pl-4" onClick={() => createIdea()}>Generate New Project Idea</button>
        </div>
     );
}

export default Terminal;
